declare module '*.svg' {
   const value: string;
   export default value;
}

declare module '*.png' {
   const value: string;
   export default value;
}

declare module '*.jpg' {
   const value: string;
   export default value;
}

declare module '*.jpeg' {
   const value: string;
   export default value;
}

declare module '*.ico' {
   const value: string;
   export default value;
}

declare module '*.module.sass' {
   const classes: { readonly [key: string]: string };
   export default classes;
}

declare module '*.module.scss' {
   const classes: { readonly [key: string]: string };
   export default classes;
}
